module.exports = function(url, callback){

	var request = require('request');
	var domain = url.match(/(http:\/\/|https:\/\/)[^\/]+/gi);
	domain = domain.length > 0 ? domain[0] : "";

	request({
	  url: url,
	  headers: {
	    'REFERER': domain
	  }
	}, function (error, response, body) {
	  callback( body );
	});


}
